import React,{ useState } from "react";
import Tuscany from '../../DB/Tuscany.json';


import clndr from "../../assets/home/zoom.svg";
import Dots from '../Carousel/Dots';
import Arrows from '../Carousel/Arrow'

export default function TuscanySlider () {
    const [currentSlide, setCurrentSlide] = useState(0);
    const tours = Tuscany[currentSlide];
    
    return(
<>

<Arrows 
   prevSlide={()=> setCurrentSlide(currentSlide < 1 ? Tuscany.length - 1 : null)}
   nextSlide = {()=> setCurrentSlide(currentSlide > 1 ? Tuscany.length + 1  : null)}
   />
   
<section className="destination" >
    
        <section className="tours" key={Tuscany.id}>
            <img className="logo_tour" src={Tuscany.img} />
            <h2>{Tuscany.title}</h2>
                <section className="dest_price" >
                    <h3> from </h3> <h2><strong>{Tuscany.price}  â‚¬</strong></h2>
                </section>
                        
                <section className="details">
                    <section className="sub_details">
                        <img className="icons" src={clndr} /><p>{Tuscany.visit} </p>
                    </section>     
                    <section className="sub_details">
                        <img className="icons" /> <p> {Tuscany.people}</p>
                    </section>
                </section>
                        <p>{Tuscany.short_description}</p>
        </section>
</section>  

{/* <Dots
 currentSlide={currentSlide}
 TuscanySlider={TuscanySlider}
 onClick={(currentSlide)=> setCurrentSlide(currentSlide)}
 /> */}
</>
    )
}
