import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    increase:null,
    decrease:null,

    adultPrice:0,
    childPrice:0,
    infant:0,
    adult
    
}

const counterSlice = createSlice({
    name:'counter',
    initialState,
    reducers:{
        setIncrease :(state) => {
            state.value -= 1;
            state.adultCost = state.value * state.adultPrice;
            state.childCost = state.value * state.childPrice;
        },
        setDescrease:(state) => {
            state.value += 1;
            state.adultCost = state.value * state.adultPrice;
        },
        setAdultPrice : (state,action) => {
            state.adultPrice = action.payload;
            state.adultCost = state.value * action.payload
        }
        
    }
})

export const {setIncrease,setDescrease} = counterSlice.actions;
export default counterSlice.reducer;
