import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Dropdown from 'react-dropdown';
import logo from '../assets/home/logo.svg';
import Home from "../pages/home";
import AboutUs from "../pages/About_Us";
import ContactUs from "../pages/Contact_Us";
import Tours from '../pages/Tours';
import CreateAccount from '../pages/authentification/CreateAccount';

export default function Header() {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const languageOptions = ['ENG', 'UKR'];

    const handleLanguageChange = (option) => {
        console.log(option.value); // Handle language change
    };

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    return (
        <header>
            <Link to="/" element={<Home />}>
                <img className="logo" src={logo} alt="Tours to Tuscany" />
            </Link>

            <nav className="setUp">
                <ul>
                    <Link to="/" element={<Home />}>
                        <li>Home</li>
                    </Link>

                    <Link to="/aboutUs" element={<AboutUs />}>
                        <li>About us</li>
                    </Link>

                    <Link to="/Tours" element={<Tours />}>
                        <li>Tours</li>
                    </Link>

                    <Link to="/contact" element={<ContactUs />}>
                        <li>Contact Us</li>
                    </Link>
                </ul>
            </nav>

            <section className="options">
                <Dropdown
                    options={languageOptions}
                    onChange={handleLanguageChange}
                    placeholder="Select Language"
                />
                <Link to="/Login">
                    <li onClick={handleOpenModal}>Login</li>
                </Link>
                <Link to="/CreateAccount" element={<CreateAccount />}>
                    <button className="signUp_btn">Sign Up</button>
                </Link>
            </section>

            {/* Modal Example */}
            {isModalOpen && (
                <div className="modal">
                    <button onClick={handleCloseModal}>Close Modal</button>
                </div>
            )}
        </header>
    );
}
