import { createSlice,createAsyncThunk } from "@reduxjs/toolkit";
import axios from 'axios';

// export const fetchTourById = createAsyncThunk(
//     'tours/fetchTourById',
//     async (id, { rejectWithValue }) => {
//       try {
//         const response = await axios.get(`http://localhost:4000/tours/bookingTourById/${id}`);
//         return response.data;
//       } catch (error) {
//         return rejectWithValue(error.response.data);
//       }
//     }
//   );

const counterSlice = createSlice({
    name: "counter",
    initialState:{
        adultPrice :0,
        childPrice :0,
        infantPrice:0,
        adultCost:0,
        childCost:0,
        infantCost:0,
        totalCost:0,
    },
    reducers: {
        setIncrease: (state) => {
            state.value += 1;
        },
        setDescrease: (state) => {
            if (state.value > 0) state.value -= 1;
        },
        setAdultCost: (state, action) => {
            state.adultPrice = action.payload.price;
            state.adultCost = action.payload.quantity * state.adultPrice;
        },
        setChildCost: (state, action) => {
            state.childPrice = action.payload.price;
            state.childCost = action.payload.quantity * state.childPrice;
        },
        setInfantCost: (state, action) => {
            state.infantPrice = action.payload.price;
            state.infantCost = action.payload.quantity * state.infantPrice;
        },
        setTotalCost: (state) => {
            state.totalCost = state.adultCost + state.childCost + state.infantCost;
        },
    },
});

export const {
    setIncrease,
    setDescrease,
    setAdultCost,
    setChildCost,
    setInfantCost,
    setTotalCost,
} = counterSlice.actions;

export default counterSlice.reducer;
