import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Async thunk to fetch tour details from the backend
export const fetchTourById = createAsyncThunk(
  'tours/fetchTourById',
  async (id, { rejectWithValue }) => {
    try {
      const response = await axios.get(`http://localhost:4000/tours/bookingTourById/${id}`);
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

const tourSlice = createSlice({
  name: 'tours',
  initialState: {
    specificTour: null,
    loading: false,
    error: null,
  },
  reducers:{},
  extraReducers:(builder)=>{
    builder
      .addCase(fetchTourById.pending,(state)=>{
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchTourById.fulfilled,(state,action)=> {
        state.loading = false;
        state.specificTour = action.payload 
      })
  }
    

  },
);

export default tourSlice.reducer;
