import { createSlice,createAsyncThunk } from "@reduxjs/toolkit";
import axios from 'axios';

// export const fetchTourById = createAsyncThunk(
//     'tours/fetchTourById',
//     async (id, { rejectWithValue }) => {
//       try {
//         const response = await axios.get(`http://localhost:4000/tours/bookingTourById/${id}`);
//         return response.data;
//       } catch (error) {
//         return rejectWithValue(error.response.data);
//       }
//     }
//   );

const counterSlice = createSlice({
    name: "counter",
    initialState:{
        adultPrice :0,
        childPrice :0,
        infantPrice:0,
        adultCost:0,
        childCost:0,
        infantCost:0,
        totalCost:0,
    },
    reducers: {
        increase: (state) => {
            state.value += 1;
        },
        descrease: (state) => {
            if (state.value > 0) state.value -= 1;
        },
        adultCost: (state, action) => {
            state.adultPrice = action.price;
            state.adultCost = action.quantity * state.adultPrice;
        },
        childCost: (state, action) => {
            state.childPrice = action.price;
            state.childCost = action.payload.quantity * state.childPrice;
        },
        infantCost: (state, action) => {
            state.infantPrice = action.payload.price;
            state.infantCost = action.payload.quantity * state.infantPrice;
        },
        totalCost: (state,) => {
            state.totalCost = state.adultCost + state.childCost + state.infantCost;
        },
    },
});

export const {
    setIncrease,
    setDescrease,
    setAdultCost,
    setChildCost,
    setInfantCost,
    setTotalCost,
} = counterSlice.actions;

export default counterSlice.reducer;
