import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

import group from '../assets/home/icons/Tours/group_col.svg';
import duration from '../assets/home/icons/Tours/duration.svg';
import location from '../assets/home/icons/Footer/location.svg';
import guide from '../assets/home/icons/guide.svg';
import lang from '../assets/home/icons/Tours/language.svg';
import fees from '../assets/home/icons/Tours/ticket.svg';
import transport from '../assets/home/icons/Tours/transport_col.svg';

import { MainGallery } from '../components/MainGallery';
import { GalleryPlaces } from '../components/GalleryPlaces';
import Testimotionals from '../components/Carousel/TestimonialsCarousel';

import back from '../assets/home/icons/CheckIn/grey_arrow.svg';
import TimePicker from '../Picker/TimePicker.jsx';
import DatePicker from '../Picker/DatePicker.jsx';

export default function SpecificTour() {
  const { id } = useParams();
  const [tour, setTour] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTour = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/tour/${id}`);
        setTour(response.data);
      } catch (err) {
        console.error('Error loading tour:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTour();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (!tour) return <p>Tour not found</p>;

  return (
    <section className='specifictour'>
      <section>
        <Link to="/tours">
          <span className='btn_back'>
            <img src={back} alt='back to the tour page' /><p>Back</p>
          </span>
        </Link>
      </section>

      <section className='main_specificTour'>
        <MainGallery />

        <section className="tour" key={tour._id}>
          <section className='spectTour_intro'>
            <h2>{tour.title}</h2>
            <section className='specTour_price'>
              <h2>from</h2> <p><strong>{tour.adultPrice} €</strong></p>
            </section>
            <h3>{tour.short_description}</h3>
          </section>

          <section className='spectTour_intro'>
            <h3>Select a date</h3>
            <DatePicker />
          </section>

          <section className='spectTour_intro'>
            <h3>Time</h3>
            <section className="book_set">
              <TimePicker />
            </section>
          </section>

          <Link to={`/BookingDetails/${tour._id}`}>
            <button className='general_btn'>Buy Now</button>
          </Link>
        </section>
      </section>

      <section className='detail_container'>
        <h3>Details</h3>
        <p>{tour.details}</p>

        <section className='detail'><img src={group} alt='group size' /> <h3>Group: {tour.people}</h3></section>
        <section className='detail'><img src={duration} alt='duration' /> <h3><strong>Duration:</strong> {tour.duration}</h3></section>
        <section className='detail'><img src={location} alt='departure' /> <h3>Departure & Arrival: {tour.departure}</h3></section>
        <section className='detail'><img src={guide} alt='guide' /> <h3>Guide service: {tour.guide}</h3></section>
        <section className='detail'><img src={lang} alt='language' /> <h3>Language: {tour.language}</h3></section>
        <section className='detail'><img src={fees} alt='fees' /> <h3>Entry Fees: {tour.fees}</h3></section>
        <section className='detail'><img src={transport} alt='transport' /> <h3>Transportation: {tour.transport}</h3></section>
      </section>

      <GalleryPlaces />
      <Testimotionals id={tour._id} />
    </section>
  );
}
