import React, { useState } from "react";
import Tuscany from '../../DB/Tuscany.json';

import leftarrow from '../../assets/home/icons/test_left.svg';
import rightarr from '../../assets/home/icons/test_right.svg';

import clndr from "../../assets/home/icons/Tours/clndr_orng.svg";
import ppl from '../../assets/home/icons/Tours/group_col.svg'

export default function TuscanySlider() {
    const [currentSlide, setCurrentSlide] = useState(0);

    // Ensure the index stays within bounds
    const handleNextSlide = () => {
        setCurrentSlide((prev) => (prev + 1  < Tuscany.length ? prev + 1 :0 ));
    };

    const handlePrevSlide = () => {
        setCurrentSlide((prev) => (prev  - 1  >= 0 ));
    };

    // const toursTuscany = Tuscany[currentSlide]; // This gets a single tour item

    return (
        <section className="general_container">
            <section className="carousel_title" >
                <h2>Explore Our Popular Destinations</h2>
                <section className="arrows">
                    <button className="prev btn" onClick={handlePrevSlide}>
                        <img src={leftarrow} alt="Previous" />
                    </button>
                    <button className="next btn" onClick={handleNextSlide}>
                        <img src={rightarr} alt="Next" />
                    </button>
                </section>
            </section>
<section className="destination_container"
>
     <section className="destination"
  >
             {Tuscany.slice(currentSlide,currentSlide + 1).map((tour)=> (   
                <section className="tours" 
                key={tour.id}>
                    <img className="logo_tour" src={tour.img} alt={tour.title} />
                    <h2>{tour.title}</h2>

                <section className="dest_price">
                        <h3>from</h3> <h2><strong>{tour.price} â‚¬</strong></h2>
                </section>

                <section className="details">
                    <section className="sub_details">
                            <img className="icons" src={clndr} alt="Calendar" />
                            <p>{tour.visit}</p>
                    </section>
                        <section className="sub_details">
                            <img src={ppl} className="icons" alt="People" />
                            <p>{tour.people}</p>
                        </section>
                </section>
                    <p>{tour.short_description}</p>
                </section>
            ))} 
            </section>
</section>
           
       
        </section>
    );
}
