import React from "react";
import {Link} from 'react-router-dom';
import ppl from "../assets/home/icons/Tours/groups.svg";
import clndr from "../assets/home/icons/Tours/date.svg";
import time from "../assets/home/icons/Tours/time.svg";
import tour from "../assets/home/icons/Tours/tour.svg";
import transport from "../assets/home/icons/Tours/transport.svg";
import search from "../assets/home/icons/Tours/zoom.svg";
import DatePicker from "../Picker/DatePicker.jsx";
import TimePicker from '../Picker/TimePicker.jsx'

import Tuscany from  '../DB/Tuscany.json';
import Search from '../components/search.jsx'

import {useDispatch,useSelector} from 'react-redux'
import { setTourType,setTime,setDate,setTour } from "../slices/tourSlice.js";


export default function TourPanel() {

    const dispatch = useDispatch();
    const {selectedTourType,selectedTour,selectedDate,selectedTime} = useSelector((state)=> state.tour);

const handleTourTypeChange = (type) => {
    dispatch(setTourType(type));
};

const handleDateChange = (date) => {
    dispatch(setDate(date));
}
const handleTimeChange = (time) => {
    dispatch(setTime(time));
}
const handleTourChange = (tour) => {
    dispatch(setTour(tour));
}


// const Transport = [
//     {id:0,vehicle: "Minivan and Bus"},
//     {id:1,vehicle:"Transfers & NCC"},
//     {id:2,vehicle:"Luxury Experience"}
// ];

// const Group = [
//     {id:0,person:'5-10'},
//     {id:1,person:'5-12'},
//     {id:2,person:'10-50'}
// ];
// const findTransport = Tuscany.filter((transport)=>  transport.id === id);
// const findGroup = Tuscany.filter((group)=> group.id === id);
// const findTours = Tuscany.filter((title)=> title.id === id);


    return(
    <>
        <section className="tour_panel">
            <section>
                 <button onClick={()=> handleTourTypeChange('Public Tours')} className="tour_option"> Public Tours</button>

                <button  onClick={()=> handleTourChange('Private Tour')} className="tour_option" > Private Tours</button>                
                    <section className="search-container" >
                     
                        {/* <section className="searchItem">
                          <img src={ppl} alt="Choose number of people" />
                            <section className="search_item_container">
                               <span>Number of people</span>
                                 <select>
                                <option disabled selected>Choose number</option>
                                {findGroup.map((person) => (
                                    <option key={person.id} value={person.person}> {person.person} 
                                    </option>
                                ))}
                                </select> 
                            </section>
                           
                        </section> */}
                        
                        <section  className="searchItem">
                            <img src={clndr}/>  
                            <section className="search_item_container">
                               <span> Date</span>
                            <select> 
                                <option>Choose date
                                     <DatePicker onChange={(e)=> handleDateChange(e.target.value)}/>
                                </option>
                            </select>
                             </section>
                        </section>

                        <section className="searchItem">
                            
                            <TimePicker onChange={(e) => handleTimeChange(e.target.value)}/>
                        </section>
            
                        {/* <section className="searchItem">
                           <img src={tour} /> 
                           <section className="search_item_container">
                            <span>Tour</span>
                            <select onChange={(e) => handleTourChange(e.target.value)} value={selectedTour || ''}>

                            <option  selected>Choose tour</option>
                            {findTours.map((tour) => (
                                <option key={tour.id} value={tour.title}>
                                {tour.title} 
                                </option>
                            ))}
                            </select> 
                             </section>
                        </section> */}

                        {/* <section className="searchItem">
                           <img src={transport} /> 
                           <section className="search_item_container">

                            <span>Transportation</span>
                              <select>
                                <option disabled selected> Choose Transport</option>
                            {findTransport.map((vehicle) => (
                            <option key={vehicle.id} value={vehicle.vehicle}>
                                {vehicle.vehicle}
                            </option>
                            ))}
                        </select>
                         </section>
                        </section>
                         */}
                        <Link to="/search" element={<Search/>}>
                        <button>
                                <img src={search} alt="search your tours"/>
                            </button>
                        </Link>
                    </section>    
            </section>  
        </section>
    </>
    )
}
