import {createSlice} from '@reduxjs/toolkit';
import Tuscany from '../DB/Tuscany.json'
const tourSlice = createSlice({
  name: "tour",
  initialState: {
    adultPrice: 0,
    childPrice: 0,
    infantPrice: 0,
    adultCost: 0,
    childCost: 0,
    infantCost: 0,
    totalCost: 0,
    value: 0,
    adultValue:0,
    img:null,

  },
  reducers: {
    setValue: (state, action) => {
      state.value = action.payload;
    },
    increase: (state) => {
      state.value += 1;
    },
    decrease: (state) => {
      if (state.value > 0) {
         state.value -= 1;
      } 
    },
    adultPrice :(state,action) => {

    },
    TotalCost: (state) => {
      state.totalCost = state.adultCost + state.childCost + state.infantCost;
    },
    ImgTour :(state,action)=> {
      const findImgById = Tuscany.find((tour)=> tour.id === action.payload) || null;
      state.img = state.findImgById;
    },

  },
});

export const {
  setValue,
  increase,
  decrease,
  setTotalCost,
  setImgTour
} = tourSlice.actions;
export default tourSlice.reducer;
