import React,{useContext, useState} from "react";
import time from '../assets/home/icons/Tours/time.svg';
import { ToursContext } from "../context/ToursContext";

export default function TimePicker () {

const [selectedTime,setSelectedTime] = useState()
const {updateToursData} = useContext(ToursContext);

const Time = [
        {id:0, label:"13:00", value: new Date().setHours(13,0,0,0)},
        {id:1, label:"15:00", value: new Date().setHours(15,0,0,0)},
        {id:2, label:"17:00", value: new Date().setHours(17,0,0,0)},
];
const handleTimechange = (e) => {
    const selectedValue = parseInt(e.target.value); // Get the value from the dropdown (as a number)
    const selectedOption = Time.find((t) => t.value === selectedValue); // Find the corresponding object
  
    if (selectedOption) {
      setSelectedTime(selectedOption.label); // Update local state
      updateToursData("selectedTime", selectedOption.label); // Update context with the selected time
    }
  };
  

// const handleTimechange = (e) => {
//         const value = parseInt(e.target.value); 
//         const selected = Time.find((t) => t.value === value);
    
//         if (selected) {
//           setSelectedTime(selected.label);
//           updateToursData("selectedTime", selected.value); 
//         }
// };

     
    return(
        <>
            <section className="custom_container">
                    <select 
                    onChange={handleTimechange}
                    value={selectedTime || ''}
                    >
                        {Time.map((time) => (
                            <option key = {time.id} value = {time.value}>
                                {time.label}
                            </option>
                        ))}
                    </select>          
                                <img src={time} alt="Choose a time"/>  
            </section>
        </>
    )
}
