import React from 'react';
import { useParams, Link } from 'react-router-dom';
import toursData from '../DB/Tuscany.json';
import BookingDetails from './Purchase/BookingDetails';

import group from '../assets/home/icons/Tours/group_col.svg';
import duration from '../assets/home/icons/Tours/duration.svg';
import location from '../assets/home/icons/Footer/location.svg';
import guide from '../assets/home/icons/guide.svg';
import lang from '../assets/home/icons/Tours/language.svg';
import fees from '../assets/home/icons/Tours/ticket.svg';
import transport from '../assets/home/icons/Tours/transport.svg';
import { MainGallery } from '../components/MainGallery';
import { GalleryPlaces } from '../components/GalleryPlaces';
import Testimotionals from '../components/testimotionals';

export default function SpecificTour() {
    const { id } = useParams();
    const tours = toursData.find((tours) => tours.id === parseInt(id));

    // Check if tours is undefined
    if (!tours) {
        return <p>Tour not found</p>;
    }
    console.log(tours)

    return (
        <>
       
        <section>
<button><img/></button>
        </section>
    <section className='specificTour_main_container'>
        <section className="tour" key={tours.id}>
                <h2>{tours.title}</h2> 
                <p>from</p> <p><strong>Price:</strong> {tours.price} â‚¬</p>
                <MainGallery/>
                <p>{tours.short_description}</p>
                <section>
                    <Link to={'/BookingDetails/${id}'}>
                        <button>Buy Now</button>
                    </Link>
                    <h3>Details</h3>
                    <p>{tours.details}</p>
                    <img src={group} alt='number of people in the group'/> 
                    <h3>Number of group: {tours.people}</h3>
                    <img src={duration} alt='trip duration'/> 
                    <h3><strong>Duration:</strong> {tours.duration}</h3>   
                    <img src={location} alt='departure area'/> 
                    <h3>Departuring and arriving areas: {tours.departure}</h3>
                    <img src={guide} alt='guide'/> 
                    <h3>Guide service: {tours.guide}</h3>
                    <img src={lang} alt='language tour'/> 
                    <h3>Language: {tours.language}</h3>
                    <img src={fees} alt='entry fee'/> 
                    <h3>Entry Fees: {tours.fees}</h3>
                    <img src={transport} alt='available transport'/>  
                    <h3>Transportation: {tours.transport}</h3>
                </section>
        </section>
            <GalleryPlaces/>   
              <Testimotionals id={id}  />
    </section>
    </>
    );
}
