import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import Tuscany from "../DB/Tuscany.json";
import { selectedDate } from "./dateSlice";
import axios from 'axios'
import tours from './fetchTours';


const tourSlice = createSlice({
  name: "tour",
  initialState: {
    ticketData: {
      adultValue: 0,
      childValue: 0,
      infantValue: 0,
      adultPrice: null,
      childPrice: Tuscany.childPrice || null,
      infantPrice: Tuscany.infantPrice || 0,
      adultCost: 0,
      totalPrice: 0,
      img: null,
      title: null,
      numberOfGroup: Tuscany.people,
      duration: Tuscany.duration,
      guide: Tuscany.guide,
      language: Tuscany.language,
      fees: Tuscany.fees,
      transport: Tuscany.transport,
      payment: null,
      statusTour: null,
    },
  },
  reducers: {
    setAdultValue: (state, action) => {
      state.adultValue = action.payload;
    },
  setAdultPrice: (state, action) => {
  const { id, tours = [] } = action.payload;
  console.log('Setting adult price for id', id);
  co
  const tour = tours.find((item) => item.id === id);
  state.ticketData.adultPrice = tour ? tour.adultPrice : null;
},

    // const { id } = action.payload;
      // const tour = Tuscany.find((item) => item.id === id);
      // state.adultPrice = tour ? tour.adultPrice : null;
    setAdultCost: (state) => {
      state.adultCost = state.adultPrice * state.adultValue;
    },
    setChildValue: (state, action) => {
      state.childValue = action.payload;
    },
    setInfantValue: (state, action) => {
      state.infantValue = action.payload;
    },
    setChildPrice: (state, action) => {
      const { id } = action.payload;
      const tour = Tuscany.find((item) => item.id === id);
      state.childPrice = tour ? tour.childPrice : null;
    },
    setInfantPrice: (state, action) => {
      const { id } = action.payload;
      const tour = Tuscany.find((item) => item.id === id);
      state.infantPrice = tour ? tour.infantPrice : 0;
    },
    setChildCost: (state) => {
      state.childCost = state.childValue * state.childPrice;
    },
    setInfantCost: (state) => {
      state.infantCost = state.infantValue * state.infantPrice;
    },
    setTotalPrice: (state) => {
      state.totalPrice = state.adultCost + state.childCost + state.infantCost;
    },
    increaseAdultValue: (state) => {
      state.adultValue += 1;
    },
    decreaseAdultValue: (state) => {
      if (state.adultValue > 0) state.adultValue -= 1;
    },
    increaseChildValue: (state) => {
      state.childValue += 1;
    },
    decreaseChildValue: (state) => {
      if (state.childValue > 0) state.childValue -= 1;
    },
    increaseInfantValue: (state) => {
      state.infantValue += 1;
    },
    decreaseInfantValue: (state) => {
      if (state.infantValue > 0) state.infantValue -= 1;
    },
    setImgTour: (state, action) => {
      const tour = Tuscany.find((tour) => tour.id === action.payload);
      state.img = tour.img || null;
    },
    setTourTitle: (state, action) => {
      const tour = Tuscany.find((tour) => tour.id === action.payload);
      state.title = tour.title || null;
    },
    setNumberOfGroup: (state, action) => {
      const groups = Tuscany.find((people) => people.id === action.payload);
      state.numberOfGroup = groups.numberOfGroup || null;
    },
    setTourDuration: (state, action) => {
      const durationTour = Tuscany.find((tour) => tour.id === action.payload);
      state.duration = durationTour.duration || null;
    },
    setGuide: (state, action) => {
      const guideInclude = Tuscany.find((guide) => guide.id === action.payload);
      state.guide = guideInclude.guide || null;
    },
    setLanguage: (state, action) => {
      const languageTour = Tuscany.find(
        (language) => language.id === action.payload
      );
      state.language = languageTour.language || null;
    },
    setTourFees: (state, action) => {
      const fees = Tuscany.find((tourFees) => tourFees.id === action.payload);
      state.fees = fees.fees || null;
    },
    setTourTransport: (state, action) => {
      state.transport = action.payload || null;
    },
    setPaymentMethod: (state, action) => {
      state.payment = action.payload;
    },
    setStatusTour: (state, action) => {
      const selectedDate = new Date(action.payload);
      const today = new Date();

      if (selectedDate > today) {
        state.statusTour = "upcoming";
      } else {
        state.statusTour = "ended";
      }
    },
    setTicketData: (state, action) => {
      state.ticketData = (action.payload);
    },
  },
});

export const {
  setTourTitle,
  setAdultValue,
  setAdultPrice,
  setAdultCost,
  setChildValue,
  setChildPrice,
  setChildCost,
  setInfantValue,
  setInfantPrice,
  setInfantCost,
  setTotalPrice,
  increaseAdultValue,
  decreaseAdultValue,
  increaseChildValue,
  decreaseChildValue,
  increaseInfantValue,
  decreaseInfantValue,
  setImgTour,
  setNumberOfGroup,
  setTourDuration,
  setLanguage,
  setTourFees,
  setTourTransport,
  setPaymentMethod,
  setStatusTour,
  setTicketData,
} = tourSlice.actions;
export default tourSlice.reducer;
