import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  getTourById,
  setTourTitle,
  setImgTour,
  setAdultPrice,
  setChildPrice,
  setInfantPrice,
} from "../redux/tourSlice";

const BookingDetails = () => {
  const { id } = useParams();
  const dispatch = useDispatch();

  const { ticketData, loading, error, currentTour } = useSelector(
    (state) => state.tour
  );

  useEffect(() => {
    if (id) {
      dispatch(getTourById(id));
    }
  }, [dispatch, id]);

  useEffect(() => {
    if (currentTour) {
      dispatch(setTourTitle(currentTour.id));
      dispatch(setImgTour(currentTour.id));
      dispatch(setAdultPrice({ id: currentTour.id, tours: [currentTour] }));
      dispatch(setChildPrice({ id: currentTour.id, tours: [currentTour] }));
      dispatch(setInfantPrice({ id: currentTour.id, tours: [currentTour] }));
    }
  }, [dispatch, currentTour]);

  if (loading) return <div className="p-4">Loading...</div>;
  if (error) return <div className="p-4 text-red-500">Error: {error}</div>;

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">{ticketData.title}</h1>
      <img
        src={ticketData.img}
        alt={ticketData.title}
        className="w-full h-auto mb-4 rounded"
      />
      <p><strong>Adult Price:</strong> ${ticketData.adultPrice}</p>
      <p><strong>Child Price:</strong> ${ticketData.childPrice}</p>
      <p><strong>Infant Price:</strong> ${ticketData.infantPrice}</p>
    </div>
  );
};

export default BookingDetails;
