import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import BookingDetails from "../pages/Purchase/BookingDetails";

 const steps = [
    { number: 1, title: "Booking Details", path: "/BookingDetails", key: "booking" },
    { number: 2, title: "Your Details", path: "/User", key: "user" },
    { number: 3, title: "Payment", path: "/Payment", key: "payment" },
  ];
export default function ProgressBar  ()  {

 
  

    return(
      <nav>
      <ul className="progress-list">
        {steps.map((step, index) => (
          <React.Fragment key={step.key}>
            <Link to={step.path}>
              <li>
                <span
                  className={`step-number ${currentStep >= step.number ? "done" : ""}`}
                >
                  {step.number}
                </span>
                <h6
                  className={`step-title ${currentStep >= step.number ? "done" : ""}`}
                >
                  {step.title}
                </h6>
              </li>
            </Link>

            {index < steps.length - 1 && (
              <section className={`path ${currentStep > step.number ? "done" : ""}`}></section>
            )}
          </React.Fragment>
        ))}
      </ul>
    </nav>
  );

}
