import React from "react";
import {Link} from 'react-router-dom';
import ppl from "../assets/home/icons/Tours/groups.svg";
import clndr from "../assets/home/icons/Tours/date.svg";
// import time from "../assets/home/icons/Tours/time.svg";
import tour from "../assets/home/icons/Tours/tour.svg";
import transport from "../assets/home/icons/Tours/transport.svg";
import search from "../assets/home/icons/Tours/zoom.svg";
import DatePicker from "../Picker/DatePicker.jsx";
import TimePicker from '../Picker/TimePicker.jsx'
import PubTours from '../assets/home/icons/Tours/publicTours.svg';
import PrivTours from '../assets/home/icons/Tours/privateTours.svg';

import Tuscany from  '../DB/Tuscany.json';
import Search from '../components/search.jsx'

// import {useDispatch,useSelector} from 'react-redux'
// import { setTourType,setTime,setDate,setTour } from "../slices/tourSlice.js";


export default function TourPanel() {

const findTour = Tuscany.map((tour)=> tour.title);
  const findTransport = Tuscany.map((tour)=> tour.transport);
  // const find1stGroup  = Tuscany.filter((tour)=> tour.people === "5-10");
  const find2ndGroup = Tuscany.filter((tour)=> tour.people === "10-50");

  const numberOfPeople = [
    {id:0,people:"5-10"},
    {id:1,people:"10-50"}
  ]
// const handleTourTypeChange


    return(
    <section className="main">
    {/* <section className="btns">
      <section className="btns_tour ">     
        <button onClick={()=> handleTourTypeChange('Public Tours')} className="tour_option"> Public Tours</button>
        <button  className="public">
          <img src={PubTours} alt="Choose a public tour"/> 
          Public Tours
        </button>
      </section>
      <section className="btns_tour"></section>
        <button  onClick={()=> handleTourChange('Private Tour')} className="tour_option" > Private Tours</button>
        <button  className="private ">
          <img src={PrivTours} alt="choose a private tour"/>
           Private Tours
        </button>
    </section> */}
    <section className="tour_panel">
      
<section className="publicTour">
          <section className="search-container">

            <section className="searchItem">
                  <img src={ppl} alt="Choose a  number of  tour's people" />
                <section className="custom_container">
                    <span>Number of people</span>
                    {/* <select>
                      <option>Choose number</option>
                        {numberOfPeople.map((person,index) => (
                          <option key={index} value={person}>
                            {person}
                          </option>
                      ))}
                       <option>Choose number</option>
                        {find2ndGroup.map((person) => (
                          <option key={person.id} value={person}>
                            {person}
                          </option>
                      ))}
                     </select> */}
                 </section>
              </section> 

              <section  className="searchItem">
                <img src={clndr} alt="choose a tour's date"/>
                  <section className="custom_container">
                    <span>Date</span>
                      {/* <select>
                        <option>Choose date
                          <DatePicker />
                        </option>
                      </select> */}
                  </section>
              </section>

              <section className="searchItem">
                        <TimePicker/>
              </section>
                        {/*Select Tour */}
              <section className="searchItem">
                <img src={tour} alt="choose a specific tour" />
                <section className="custom_container">
                <span>Tour</span>
                  {/* <select>
                    {findTour.map((tour, index) => (
                    <option key={index} value={tour}>
                      {tour}
                    </option>
                  ))}
                  </select> */}
                  </section>
              </section>

              <section className="searchItem">
                <img src={transport} alt="choose a tour's transport" />
                <section className="custom_container">
                  <span>Transportation</span>
                    {/* <select>
                      {findTransport.map((tour, index) => (
                        <option  selected
                        key={index} value={tour}>
                          {tour}
                        </option>
                      ))}
                    </select> */}
                  </section>
              </section>   
                        <Link to="/search" element={<Search/>}>
                        <button className="general_btn">
                                <img src={search} alt="search your tours"/>
                            </button>
                        </Link>
          </section>
      </section>
    
    </section>
    </section>
    )
}
