import React,{useState} from "react";
import Tuscany from '../DB/Tuscany.json';


// import grp from '../assets/home/icons/grp.png';
import drtn from '../assets/home/icons/icons_tours/duration.svg'
import lctn from '../assets/home/icons/location.svg';
import gd from '../assets/home/icons/icons_tours/guide.svg'
import ln from '../assets/home/icons/icons_tours/language.svg'
import fs from '../assets/home/icons/icons_tours/ticket.svg'
// import trnsprt from '../assets/home/icons/icons_tours/transport.svg'
import DatePicker from "../components/DatePicker/DatePicker";



export default function SpecificTour () {   
    const [startDate, setStartDate] = useState(new Date());
    const [selectedTour, setSelectedTour] = useState(null);

    useEffect(() => {
      const foundTour = Tuscany.find(tour => tour.id === Number(id));
      setSelectedTour(foundTour);
    }, [id, Tuscany]);

    return(
        <section>
            <section>
                
                    <section key={tour.id}>
                        <img src={tour.img} alt={tour.title}/>
                        <h3> {tour.title}</h3>
                        <section className="price">
                            <p>from <p>{tour.price}</p></p>
                        </section>
                            <p>{tour.short_description}</p>

                            <lable>Date</lable>
                            <section className="date-picker">
                               <DatePicker/>
                                    
                            </section>
                
                            <lable>Time</lable>
                                <section>   
                                </section>
                            <button> Buy Now</button>
                                <section>
                                    <h3>Details</h3>
                                    <p>{tour.details}</p>
                                    <section>
                                        {/* <img src={grp} alt="numberOfPeople" /> <p> Number of group:{Tuscany.people}</p> */}
                                    </section>
                                    <section>
                                        <img src={drtn} alt="Duration of tour"/> <p> Duration:{tour.duration}</p>
                                    </section>
                                    <section>
                                        <img src={lctn} alt="Departurting from"/> <p> Departuring and arriving areas:{tour.departure}</p>
                                    </section>
                                    <section>
                                        <img src={gd} alt="Guide"/> <p> Guide service:{tour.service}</p>
                                    </section>
                                    <section>
                                        <img src={ln}  alt="Language"/> <p> Language:{tour.language}</p>
                                    </section>
                                    <section>
                                        <img src={fs} alt="Fees" /> <p> Entry Fees:{tour.fees}</p>
                                    </section>
                                    <section>
                                        {/* <img src={trnsprt} alt="Transport" /><p> Entry Transportation:{Tuscany.transport}</p> */}
                                    </section>
                                </section>
                    </section>  
            </section>   : (<p>Tour isn't exist</p>) })
              
        </section>
    )
}
