import React, { useState } from "react";
import arr_left from '../../assets/home/test_left.svg';
import arr_r from '../../assets/home/test_right.svg';

export default function Calendar () {

   
    const days = [
        'Sun','Mon','Tue','Wed','Thu','Fri','Sat'
    ];

    const months = ['January','February','March','April','May','June',
                    'July','August','September','October','November','December'];


        const currentdDay= useState(new Date()) ;
    

const firstDayOfMonth = new Date(this.day.getFullYear(),this.day.getMonth(),1);
const weekdayOfFirstDay = firstDayOfMonth.getDay();
const currentDays = [];

for (let day = 0; day < 42; day++) {
    if (day === 0 && weekdayOfFirstDay === 0) {
      firstDayOfMonth.setDate(firstDayOfMonth.getDate() - 7);
    } else if (day === 0) {
      firstDayOfMonth.setDate(firstDayOfMonth.getDate() + (day - weekdayOfFirstDay));
    } else {
      firstDayOfMonth.setDate(firstDayOfMonth.getDate() + 1);
    }

const calendarDay = {
    currentMonth : (firstDayOfMonth.getMonth() === this.day.getMonth()),
    date:(new Date(firstDayOfMonth)),
    month: firstDayOfMonth.getMonth(),
    selected: (firstDayOfMonth.toDateString() === this.day.toDateString()),
    year: firstDayOfMonth.getFullYear()
}
currentDays.push(calendarDay)

    return(
        <section className="calendar">
            <section className="calendar-header">
            <h3>{this.months[this.state.curretndDay.getMonth()]} {this.state.curretndDay.getFullYear()}</h3>
                <section>
                    <button><img src={arr_left}/></button>
                    <button><img src={arr_r}/> </button> 
                </section> 
            </section>
            <section >
                {
                    this.days.map((days)=>{
                        <section className="calendar-subheader">
                          <p>{days}</p>  
                            
                           {currentDays.map((day)=> {
                       
                       <section className={"calendar-day" + (day.currentMonth ? "current" : "") + (day.selected ? " selected" : "")}
                            onClick={() => this.changeCurrentDay(day)}
                            >
                                {day.number}
                            </section>
                           })} 
                        </section>
                    })
                }
            </section>
        </section>
    )
}
}
