import { Link, useLocation } from "react-router-dom";


export default function ProgressBar  ()  {

const location = useLocation();

const nextStep = (path) => {
  location.pathname === path ? "step active" :"step";
}
 
    return(
            
        <>       
      <section className="progress_step_container">
        <Link to='/BookingDetails'>
                <section className={nextStep("/BookingDetails")}>
             <p>1</p> 
          <span> Booking details </span>
        </section> 
        </Link>
 <Link to='User'>
 <section className="">
             <p>2</p> 
          <span> User Details </span>
        </section> 
 </Link>
          <Link to=''> 
          <section className="step">
             <p>3</p> 
          <span> Payment  </span>
        </section>
          </Link>
       
      </section>
     
      
     
     </>

    )
}
