import React, { useState } from "react";
import { useContext } from "react";
import { useBook } from "../../context/BookContext";
import { DateContext } from "../../context/DateContext";
import TimePicker from "../../Picker/TimePicker";

export default function BookingDetails() {
    // const { addToTicket, selectedDate, setSelectedDate, selectedTime, setSelectedTime } = useBook();
    // const [ticket, setTicket] = useState(0);

    // const increaseTicket = () => setTicket(ticket + 1);
    // const decreaseTicket = () => setTicket(ticket > 0 ? ticket - 1 : 0);

    // const handleAddTicket = () => {
    //     addToTicket({
    //         date: selectedDate,
    //         time: selectedTime,
    //         quantity: ticket,
    //     });
    // };

    return (
        <>
            <progress value={30} max={100} />
            <section className="bookingDetails">
                <section className="details">
                    <label>When you visit?</label>
                    <input
                        type="date"
                        value={selectedDate || ""}
                        onChange={(e) => setSelectedDate(e.target.value)}
                    />
                </section>

                <section className="details">
                    <label>Which time?</label>
                    {/* <input
                        type="time"
                        value={selectedTime || ""}
                        onChange={(e) => setSelectedTime(e.target.value)}
                    /> */}
                    <options>
                        <p>
                            {TimePicker.selectedTime}
                        </p>
                    </options>
                </section>

                <section className="tickets_order">
                    <h2>Adult(18+)</h2>
                    <section className="tickets_counter">
                        <button onClick={decreaseTicket}>-</button>
                        <input value={ticket} readOnly />
                        <button onClick={increaseTicket}>+</button>
                    </section>
                    <button onClick={handleAddTicket}>Add Ticket</button>
                </section>

                <TicketOverview />
            </section>
        </>
    );
}
function TicketOverview() {
    const { ticketData } = useBook();
    const { selectedDate, selectedTime } = useContext(DateContext);


    return (
        <section>
            <h2>Your Ticket Overview</h2>
            {ticketData.map((item, index) => (
                <section key={index}>
            <p>Date: {selectedDate ? selectedDate.toLocaleDateString() : "Not selected"}</p>
            <p>Time: {item.time || "Not selected"}</p>
                    <p>Quantity: {item.quantity}</p>
                </section>
            ))}
        </section>
    );
}
