import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// AsyncThunk для отримання одного туру
export const fetchTourById = createAsyncThunk(
  "tour/fetchTourById",
  async (id) => {
    const response = await axios.get(`http://localhost:5000/tour/${id}`);
    return response.data;
  }
);


const tourSlice = createSlice({
  name: "tour",
  initialState: {
    ticketData: {
      adultValue: 0,
      childValue: 0,
      infantValue: 0,
      adultPrice: null,
      childPrice: null,
      infantPrice: null,
      adultCost: 0,
      childCost: 0,
      infantCost: 0,
      totalPrice: 0,
      img: null,
      title: null,
    },
    loading: false,
    error: null,
  },
  reducers: {
    
    // setAdultValue: (state, action) => {
    //   state.ticketData.adultValue = action.payload;
    // },
    // setChildValue: (state, action) => {
    //   state.ticketData.childValue = action.payload;
    // },
    // setInfantValue: (state, action) => {
    //   state.ticketData.infantValue = action.payload;
    // },
    // increaseAdultValue: (state) => {
    //   state.ticketData.adultValue += 1;
    // },
    // decreaseAdultValue: (state) => {
    //   if (state.ticketData.adultValue > 0) state.ticketData.adultValue -= 1;
    // },
    // increaseChildValue: (state) => {
    //   state.ticketData.childValue += 1;
    // },
    // decreaseChildValue: (state) => {
    //   if (state.ticketData.childValue > 0) state.ticketData.childValue -= 1;
    // },
    // increaseInfantValue: (state) => {
    //   state.ticketData.infantValue += 1;
    // },
    // decreaseInfantValue: (state) => {
    //   if (state.ticketData.infantValue > 0) state.ticketData.infantValue -= 1;
    // },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchTourById.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchTourById.fulfilled, (state, action) => {
        const tour = action.payload;
        state.loading = false;
        state.ticketData = {
          ...state.ticketData,
          adultPrice: tour.adultPrice,
          childPrice: tour.childPrice,
          infantPrice: tour.infantPrice,
          img: tour.img,
          title: tour.title,
        };
      })

      .addCase(fetchTourById.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      })
     
  },
});

export default tourSlice.reducer;

export const {
  setAdultValue,
  setChildValue,
  setInfantValue,
  increaseAdultValue,
  decreaseAdultValue,
  increaseChildValue,
  decreaseChildValue,
  increaseInfantValue,
  decreaseInfantValue,
} = tourSlice.actions;
