import React, { useState } from 'react';
import {Link} from 'react-router-dom';
import '../styles/header.css'
import Dropdown from 'react-dropdown'
import logo from '../assets/home/logo.svg';
import Home from "../pages/home";
import createAccount from '../pages/authentification/CreateAccount'

import AboutUs from "../pages/About_Us";
import ContactUs from "../pages/ContactUs";
import Login from '../pages/authentification/Login';
import Tours from '../pages/Tours';

export default function Header () {

const languageOptions = [
    'ENG','UKR'
];

const [isModalOpen,setIsModalOpen] = useState(false);
const handleModalOpen = () => {
    setIsModalOpen(true);
};
const handleModalClose = () => {
    setIsModalOpen(false);
};


    return(
    
        <header >
            <img className="logo" src={logo} alt="Tours to Tuscany"/>
           <section className='setUp'>
            <nav>
                <Link to="/" element={<Home/>}>
                <li>Home</li>
            </Link>
                
                <Link to="/aboutUs" element={<AboutUs/>}>
                <li>About us</li>
                </Link>
                
                <Link to="/Tours" element={<Tours/>}> 
                    <li> Tours</li>
                </Link>
               <Link to="/Contact" element={<ContactUs/>}>
                <li> Contact Us</li>
               </Link>
            </nav>
           
            
            <section className="options">
                <Dropdown options={languageOptions} onChange={languageOptions}/>
                
                <Link to="/login" element={<Login/>} >
                    <li>Login</li>
                </Link>
            
                <Link to="/account" element={<createAccount/>}>
                    <button className='signUp_btn' onClick={handleModalOpen}>Sign Up</button>
                    {isModalOpen && <createAccount isOpen={isModalOpen} onClose={handleModalClose}/>}
                </Link>
            </section>
           </section>
                
        </header>
    )
}
