import React, { useState } from "react";

import {useForm} from 'react-hook-form';
import {Link, useNavigate } from 'react-router-dom'
import Login from "./Login";
import NoTickets from '../account/NoTickets.jsx';
import ggl from '../../assets/home/icons/CheckIn/google.svg'

export default  function CreateAccount () {
const[user,setUser] = useState({
    username:"",
    email:"",
    password:"",
});

const navigate = useNavigate();

const handleOnchange = (e) => {
    const {name,value} = e.target;
    setUser({...user, [name] : value})
}

const syncData = async(user) => {
    try{
        const response = await fetch('http://localhost:5050/auth/createAccount/', {
            method:'POST',
            headers:{
                "Content-Type": "application/json"
            },
            body:JSON.stringify(user),
        });
        if(response == true) {
            navigate(<NoTickets/>)
        }
    } catch(e){
        throw new Error('Помилка синхронізації',e.message);
    }
}
  
     return(
<>
   <section className="auth">
            <h1>Create Account</h1>
            <form action="/signup" method="post">
                <label>Name and Surname</label>
                    <input 
                        name="NameandSurname"
                        value={user.username}
                        onChange={handleOnchange}
                        placeholder="Enter your name and surname"
                    />

                <label>Email Address</label>
                    <input
                        type="email"
                        name="email"
                        onChange={handleOnchange}
                        value={user.email}
                        placeholder="Enter your email address"
                    />
                <label>Password</label>
                <input 
                    type="password"
                    name="password"
                    onChange={handleOnchange}
                    value={user.password}
                    aria-describedby="password"
                    aria-invalid="false"
                    placeholder="Enter your password"
                />

                <section className="checkbox">
                    <input required type="checkbox"/> 
                    <p>I agree with <span className="highlight"> Terms </span>and <span className="highlight">Privacy</span></p>
                </section>
                
                    <section className="form" method='get'>
                            <button className="general_btn" type="submit" > <span>sign up</span> </button>
                            <p>or</p>
                        <button className="btn_ggl_submit" type="submit" placeholder="Sign Up with Google"> 
                            <img src={ggl} alt="signIn with Google"/> 
                            <span>Sign In with Google</span>  
                        </button>
                    <section className="additionals">
                        <p>Already have an account?</p>
                            <Link to='/auth/login'element={<Login/>}>
                                <span>Log In</span>
                            </Link>
                    </section>
                    
                    </section>
            </form>
    </section>
</>
    )
}
