import React, { useState } from "react";

const Calendar = () => {

    const [currentDate,setCurrentDate] = useState(new Date());

    const startOfMonth = () => {
        new Date (currentDate.getFullYear(),currentDate.getMonth(),1);
    }  
    const endOfMonth = new Date(currentDate.getFullYear(),currentDate.getMonth() + 1,0);

    startOfMonth <= endOfMonth.getDate ? endOfMonth ++ : daysInMonth.push(endOfMonth)

    const daysInMonth = Array.from({ length: totalDays }, (_, index) =>
        index < startOfMonth.getDay() ? null : index - startOfMonth.getDay() + 1
      );
const totalDays = startOfMonth.getDay() + endOfMonth.getDate();


    const handlePrevMonth  = () => {
        setCurrentDate(new Date(currentDate.getFullYear(),currentDate.getMonth() -1,1))
    }; 
    const handleNextMonth = () => {
        setCurrentDate(new Date(currentDate.getFullYear(),currentDate.getMonth() + 1, 1))
    } ;


    return(
        <section className="calendar">
            <section className="calendar-header">
                <section className="calendar-controllers">
                     <button onClick={handlePrevMonth}>
                    Back
                </button>
                <button onClick={handleNextMonth}>
                    Go
                </button>
                </section>
               <h2>{currentDate.toLocaleDateString}</h2>
            </section>
            <section className="calendar-body">
                {["Sun","Mon","Tue","Wed","Thu","Fri","Sat"].map((day,index)=> {
                    <section key={day} className="calendar-day"> 
                        {day}
                    </section>
                })}
            </section>
            {daysInMonth.map((day, index) => (
          <section key={index} className="calendar-day">
            {day ? day : ""}
          </section>
        ))}
      </section>


      
    )
}
