import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import tour from "../DB/Tuscany.json";
import PhotoSwipeLightbox from "photoswipe/lightbox";
import "photoswipe/style.css";

export const MainGallery = () => {
  const { id } = useParams();
  useEffect(() => {
    let lightbox = new PhotoSwipeLightbox({
      gallery: "#main-gallery", 
      children: "a",
      showHideAnimationType: 'zoom',
      pswpModule: () => import("photoswipe"),
    });

    lightbox.init();

    return () => {
      lightbox.destroy();
      lightbox = null;
    };
  }, []);

  const gallery = tour.find((gallery) => gallery.id === parseInt(id));

  if (!gallery) {
    return <p>Gallery not found</p>;
  }

  const images = [
    { id: 0, src: gallery.img, alt: "Gallery place 0"},
    { id: 1, src: gallery.img1, alt: "Gallery place 1"},
    { id: 2, src: gallery.img2, alt: "Gallery place 2"},
    { id: 3, src: gallery.img3, alt: "Gallery place 3", }
  ];
  


  return (
    <section className="main_gallery" id="main-gallery">
  <a
    href={gallery.img}
    data-pswp-width="1400"
    data-pswp-height="933"
    target="_blank"
    rel="noreferrer"
    style={{ display: "contents" }}
  >
    <img src={gallery.img} style={{ height: "600px", width: "700px",borderRadius:'0.8em' }} alt="Main" />
  </a>

  <section style={{ display: "flex", gap: "1.4em", }}>
    {[gallery.img1, gallery.img2, gallery.img3].map((src, idx) => (
      <a
        key={idx}
        href={src}
        data-pswp-width="1400"
        data-pswp-height="933"
        target="_blank"
        rel="noreferrer"
        style={{ display: "contents" }}
      >
        <img src={src} style={{ height: "140px", width: "220px" ,borderRadius:'0.8em'}} alt={`Gallery ${idx + 1}`} />
      </a>
    ))}
  </section>
</section>

      
  );
  
};
