import React from 'react';
import tour from '../DB/Tuscany.json';

export default function specificTour() {
const {id} = useParams();

  if (!tour || tour.length === 0) {
    return <p>Loading tours...</p>;
  }


  return (<section>
    
       <section className="tour" key={tour.id}>
      <h2>{tour.title}</h2>
      <img src={tour.img} alt={tour.title} />
      <p>{tour.short_description}</p>
      <p>{tour.details}</p>
      <p><strong></strong> {tour.price}</p>
      <p><strong></strong> {tour.duration}</p>
    </section>
  
  </section>
   
      
   
  );
}
