import React, { useState } from "react";
import testimotional from '../DB/Testimotional.json';
import back from '../assets/home/test_left.svg'
import right from '../assets/home/test_right.svg'

export default function Testimotionals (){


const [activeIndex,setActiveIndex] = useState(1);
const nextSlide = () => {
    setActiveIndex((prevIndex) => (prevIndex + 1) % testimotional.length );
}
const prevSlide = () => {
    setActiveIndex((prevIndex)=> (prevIndex - 1) % testimotional.length)
}
    return(
       <section >
        <section  className="btns">
          <button className="back" onClick={prevSlide}><img src={back}/></button>
          <button className="next" onClick={nextSlide}><img src={right}/> </button>
        </section>
        <h1><strong>Happy Customers Says</strong></h1>
        <section className="testimotionals">
        {testimotional.map((testimonial, index) => (
          <section 
            key={testimonial.id}
            className="testimonial"
            // className={`testimonial ${index === activeIndex ? "active" : ""}`}
          >
            <img className="test_img" src={testimonial.image} alt={testimonial.name} />
            <h3 className="name">{testimonial.name}</h3>
            <h4 className="location">{testimonial.from}</h4>
            <p className="review">{testimonial.reviews}</p>
          </section>
        ))}
      </section>
        
       </section>
    )
}
