import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { setDate } from "../slices/dateSlice";

export default function DatePicker() {
  const currentMonth = new Date().getMonth();
  const currentYear = new Date().getFullYear();
  const daysOfMonth = new Date(currentYear, currentMonth + 1, 0).getDate();

  const weekdays = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
  const months = [
    "January", "February", "March", "April", "May", "June", "July", "August", "September", "October",
    "November", "December"
  ];

  const daysArray = [...Array(daysOfMonth).keys()].map((day) => {
    const choosedDate = new Date(currentYear, currentMonth, day + 1);
    return { day: day + 1, fullDate: choosedDate.toDateString() };
  });

  const selectedDate = useSelector((state) => state.datepicker.selectedDate);
  const dispatch = useDispatch();

  return (
    <section>
      <h1>Select Date</h1>
      <section>
        <h3>{months[currentMonth]} {currentYear}</h3>
        <section>
          {weekdays.map((day, index) => (
            <span key={index} style={{ margin: "5px", fontWeight: "bold" }}>{day}</span>
          ))}
        </section>
        <section style={{ display: "flex", flexWrap: "wrap", gap: "5px" }}>
          {daysArray.map(({ day, fullDate }) => (
            <span
              key={`${currentYear}-${currentMonth + 1}-${day}`} // більш стабільний key
              style={{
                padding: "5px",
                border: "0.5em white ",
                cursor: "pointer",
                background: selectedDate === fullDate ? "#FA8B02" : "white",
              }}
              onClick={() => dispatch(setDate(fullDate))}
            >
              {day}
            </span>
          ))}
        </section>
      </section>
      <h3>Selected Date: {selectedDate || "None"}</h3>
    </section>
  );
}
