import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { Icon } from "@iconify/react";
import { fetchTourById } from "../slices/tourById";

export default function TicketOverview({ nextLink, onNext, tourId, error, loading }) {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    if (tourId) {
      dispatch(fetchTourById(tourId));
    }
  }, [dispatch, tourId]);

  const { selectedDate, selectedTime } = useSelector((state) => state.datepicker);
  const {
    adultValue,
    childValue,
    infantValue,
    totalAdultPrice,
    totalChildPrice,
    totalInfantPrice,
    totalPrice,
  } = useSelector((state) => state.tour);


  const ticketData = useSelector(state => state.tour.ticketData);
  useEffect(() => {
    if (id) {
      dispatch(fetchTourById(id))
        .unwrap()
        .then((data) => console.log("✅ Tour fetched:", data))
        .catch((err) => console.error("❌ Fetch error:", err));
    }
  }, [dispatch, id]);

useEffect(() => {
  if (tourId) {
    dispatch(fetchTourById(tourId))
      .unwrap()
      .then((data) => console.log("✅ Tour fetched:", data))
      .catch((err) => console.error("❌ Fetch error:", err));
  }
}, [dispatch, tourId]);

  if (!ticketData) return <p>Loading tour data...</p>;


  const handleClick = () => {
    if (onNext) onNext();
    navigate(nextLink);
  };

  if (loading) return <p>Loading...</p>;

  if (error)
    return (
      <p>
        Error:{" "}
        {typeof error === "string" ? error : error.message || JSON.stringify(error)}
      </p>
    );

  return (
