    //     />
    //     <section style={{ display: "flex", flexDirection: "column", gap: "0.3em" }}>
    //       <h2>{ticketData.title}</h2>
    //       <section style={{ display: "flex", gap: "0.625em", alignItems: "center" }}>
    //         <Icon icon="system-uicons:calendar-month" color="#FA8B02" />
    //         <h3>{selectedDate}</h3>
    //       </section>
    //       <section style={{ display: "flex", gap: "0.625em", alignItems: "center" }}>
    //         <Icon icon="system-uicons:clock" color="#FA8B02" />
    //         <h3>{selectedTime}</h3>
    //       </section>
    //     </section>
    //   </section>

    //   <hr />

    //   <section>
    //     {/* Adult */}
    //     <section className="details">
    //       <section className="dest_price">
    //         <section className="pplAmount"><p>{adultValue}</p></section>
    //         <p style={{ color: "#333" }}>Adult (18+) (€{ticketData.adultPrice})</p>
    //       </section>
    //       <h3>€{totalAdultPrice}</h3>
    //     </section>

    //     {/* Child */}
    //     <section className="details">
    //       <section className="dest_price">
    //         <section className="pplAmount"><p>{childValue}</p></section>
    //         <p style={{ color: "#333" }}>Child (6-17) (€{ticketData.childPrice})</p>
    //       </section>
    //       <h3>€{totalChildPrice}</h3>
