import React, { createContext, useState } from "react";
import tours from '../DB/Tuscany.json';
import {totalPrice} from '../pages/Purchase/BookingDetails'

export const ToursContext = createContext(null);

export const TourProvider = ({children}) => {

    const [bookedTours,setBookesTours] = useState([]);

    const [toursData,setToursData] = useState({
        ticket:{
            adult:0,
            child:0,
            infant:0
        },
        id:tours.id,
        selectedDay:null,
        selectedTime:null,
        adultPrice:tours.adultPrice,
        chlidPrice:tours.childPrice,
        infant:tours.infantPrice,
        title:tours.title,
        img:tours.img,
    });

    const updateToursData = (type,value) => {
        setToursData((prevData)=> ({...prevData,
            ticket:{
                ...prevData.ticket,[type]:Math.max(value,0),
            },
            }));
    };

    const bookTour = (tourId,paymentMethod,status) => {
        setBookesTours((prevBooked)=> [
            ...prevBooked,{
                id:tours.id,
                title:tours.title,
                img:tours.img,
                time:toursData.selectedTime,
                date:toursData.selectedDay,
                price:toursData.totalPrice,
                paymentMethod,
                status
            }
        ])
    }
        return(
            <ToursContext.Provider value={{toursData,bookedTours,updateToursData,bookTour}}>
                {children}
            </ToursContext.Provider>
        )
}
