import React, { useState } from "react";

import {useForm} from 'react-hook-form';
import {Link } from 'react-router-dom'
import Login from "./Login";
import ggl from '../../assets/home/icons/CheckIn/google.svg'

export default  function CreateAccount () {

    // const Login = () => {
    //     const [input,setInput] = useState({
    //         NameAndSurname:"",
    //         password:"",
    //     });
    // const handleSubmitEvent  = (e) => {
    //     e.preventDefault();
    //     if(input.NameAndSurname !== "" % input.password != "") {

    //     }
    //     alert("Please,provide a valid input");
    // }
    // }

    // const handleInput = (e) => {
    //     const {name,value} = e.target;
    //     setInput((prev)=> ({
    //         prev,
    //         [name]:value
    //     }));
    // };
    
    //connect with backen via fetch
// fetch('http://localhost:3000/') {

// }
 
     return(
     <>
   <section className="modal" >
            <h1>Create Account</h1>
            <form action="/signup" method="post">
                <label>Name and Surname</label>
                    <input 
                    name="NameandSurname"
                    placeholder="Enter your name and surname"/>

                <label>Email Address</label>
                    <input
                    type="email"
                    name="email"
                    placeholder="Enter your email address"
                    required 
                    />
                <label>Password</label>
                <input 
                    type="current-password"
                    name="password"
                    aria-describedby="password"
                    aria-invalid="false"
                    required
                    placeholder="Enter your password"/>

                <section className="checkbox">
                    <input type="checkbox"/> 
                    <p>I agree with <span className="highlight"> Terms </span>and <span className="highlight">Privacy</span></p>
                </section>
                
               <section className="form">
                     <button className="btn_submit" type="submit" ><span>sign up</span> </button>
                     <p>or</p>
                  <button className="btn_ggl_submit" type="submit" placeholder="Sign Up with Google"> 
                    <img src={ggl} alt="signIn with Google"/> 
                    <span>Sign In with Google</span>  
                    </button>
               <section className="additionals">
                <p>Already have an account?</p>
                    <Link to="/login" element={<Login/>}>
                        <span>Log In</span>
                    </Link>
               </section>
               
                </section>
            </form>
    </section>
     
          </>
    )
}
