import React, { useState } from "react";
import testimotionalData from '../DB/Testimotional.json';
import back from '../assets/home/icons/test_left.svg'
import right from '../assets/home/icons/test_right.svg'
import { useParams } from "react-router-dom";

export default function Testimotionals (){

const [currentSlide,setCurrentSlide] = useState(0);


const handleNextSlide = () => {
  setCurrentSlide((prevSlide)=> prevSlide === testimotionalData.length -1 ? 0 : prevSlide + 2);
};
const handlePrevSlide = () => {
  setCurrentSlide((prevSlide) => (prevSlide === 0 ? testimotionalData.length - 2 : prevSlide - 2));

}
const {id} = useParams();
const testimonial = testimotionalData.find((review)=> review.id === parseInt(id))

    return(
       <section >
        <section  className="btns">
          <button className="back" onClick={handlePrevSlide}><img src={back}/></button>
          <button className="next" onClick={handleNextSlide}><img src={right}/> </button>
        </section>
        <h1><strong>Happy Customers Says</strong></h1>
        <section className="testimotionals">
          <section 
            key={testimonial.id}
            className={`testimonial ${index === currentSlide ? 'active' : index === currentSlide + 1 ? 'next' : 'prev'}`}>
            <img className="test_img" src={testimonial.image} alt={testimonial.name} />
            <h3 className="name">{testimonial.name}</h3>
            <h4 className="location">{testimonial.from}</h4>
            <p className="review">{testimonial.reviews}</p>
          </section>
        
      </section>
        
       </section>
    )
}
