import React, { useState } from "react";
import reset from '../../assets/home/icons/CheckIn/password_check.svg';
import arrow from '../../assets/home/icons/CheckIn/arrow-left.svg'
import { Link } from "react-router-dom";
import Login from "./Login";
import CheckEmail from "./CheckEmail";
import { setNewPassword } from "../../slices/authSlice";

export default function ResetPassword () {

    const [email,setEmail] = useState('');

    const handleSubmit = async(e) => {

        e.preventDefault();
        //Відправка даних у Redux
        dispatch(setEmail({email}));
       }
       //Механіз подальшої навігації
       if(ema) {
        navigate('/account/haveTickets')
    }
    return(
        <section className="auth">  
            <form
            onSubmit={handleSubmit}
             method="post" action="'/auth/resetPassword">
                <img className="form_img" src={reset} alt="reset password"/>
                <h1>Forgot Password</h1>
                <h4>No worries, we'll send you reset instructions.</h4>
            
                <label for="email">Email Address</label>
                <input 
                type="email" 
                onChange={(e)=> setEmail(e.target.value)}
                placeholder="Enter your email address"
                required/>
                <Link to="/auth/checkemail" element={<CheckEmail/>}>
                    <button 
                    className="general_btn"
                    type="submit"
                    >
                        Reset Password
                    </button>
                </Link>
                <section className="additionals">
                    <Link to="/auth/login" element={<Login/>}>
                        <button>
                            <img src={arrow} alt="back to login"/> Back to Login
                        </button>
                    </Link>
                </section>
            </form>
            
         </section>
    )
}
