import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { setNewUser } from "../../slices/authSlice.js";
import ggl from "../../assets/home/icons/CheckIn/google.svg";

export default function CreateAccount() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Локальний стан
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");

  // Отримання даних зі стору
  const { user, error } = useSelector((state) => state.auth);

  useEffect(() => {
    if (user) {
      navigate("/account/noTickets");
    }
  }, [user, navigate]);

  const name = useSelector((state)=> state.auth.name)
  // const handleSubmit = async (e) => {
  //   e.preventDefault();
  //   try{
  //     const req = await axios.post('http://localhost:3000/auth/createAccount',{
  //       {name,surname,email,password}
  //     });
  //     dispatch(setNewUser({ email, password, username }));
  //   } catch(err) {
  //     console.log(err);
  //     alert('Something went wrong')
  //   }
  // };

  return (
    <section className="auth">
      <h1>Create Account</h1>
      {/* <form onSubmit={handleSubmit}> */}
      <form>
        <label htmlFor="name">Name and Surname</label>
        <input
          value={username}
          name="name"
          placeholder="Enter your name and surname"
          onChange={(e) => setUsername(e.target.value)}
        />
        <label htmlFor="email">Email Address</label>
        <input
          type="email"
          name="email"
          onChange={(e) => setEmail(e.target.value)}
          value={email}
          placeholder="Enter your email address"
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          name="password"
          aria-describedby="password"
          aria-invalid="false"
          onChange={(e) => setPassword(e.target.value)}
          value={password}
          placeholder="Enter your password"
        />
        <section className="checkbox">
          <input required type="checkbox" />
          <p>
            I agree with <span className="highlight">Terms</span> and{" "}
            <span className="highlight">Privacy</span>
          </p>
        </section>
        <section className="form">
          <button className="general_btn" type="submit">
            <span>Sign Up</span>
          </button>
          <p>or</p>
          <a href="http://localhost:3000/auth/google">
            <button className="btn_ggl_submit" type="submit">
              <img src={ggl} alt="signIn with Google" />
              <span>Sign In with Google</span>
            </button>
          </a>
          <section className="additionals">
            <p>Already have an account?</p>
            <Link to="/auth/login">Log In</Link>
          </section>
        </section>
      </form>
      {/* </form> */}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </section>
  );
}
