import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  users: [],
  id: 0,
  name:null,
  surname:null,
  fullName:null,
  email: "",
  phone:0,
  password: "",
  currentUser: null,
  isNewUser:true,
  isLoggedIn:false,
};

const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    setName:(state,action) => {
      state.name = action.payload;
    },
    setSurname:(state,action) => {
      state.surname = action.payload;
    },
    setFullName : (state,action) => {
      // if (action.payload && action.payload.name && action.payload.surname) {
      //   const { name, surname } = action.payload;
      //   state.fullName = `${name} ${surname}`;
      // } else {
      //   state.fullName = '';
      // }
      state.fullName = action.payload;
    },
    setEmail: (state, action) => {
      state.email = action.payload;
    },
    setPhone:(state,action) => {
      state.phone = action.payload;
    },
    setPassword: (state, action) => {
      state.password = action.payload;
    },

    /* Create Account */
    setNewUser: (state, action) => {
      const newUser = {
        id: state.users.length + 1, 
        fullName: state.fullName,
        email: state.email,
        password: state.password,
        isNewUser:true,
        isLoggedIn:true,
      };
      state.users.push(newUser);
      state.fullName = "";
      state.email = "";
      state.password = "";
    },

    /* Login */
    setCreatedUser: (state, action) => {
      const { email, password } = action.payload;
      const user = state.users.find(user => user.email === email && user.password === password);
      state.currentUser = user || null; 
    },
    /* Reset Password */
    setNewPassword: (state, action) => {
      const { id, newPassword } = action.payload;
      const user = state.users.find(user => user.id === id);

      if (user) {
        user.password = newPassword;
      }
    }
  }
});

export const { setName,setSurname,setFullName,setEmail, setPassword, setNewUser, setCreatedUser, setNewPassword, setPhone } = authSlice.actions;
export default authSlice.reducer;
