import {createSlice} from '@reduxjs/toolkit';
import Tuscany from '../DB/Tuscany.json'
const tourSlice = createSlice({
  name: "tour",
  initialState: {
    value:0,
    adultValue:0
  },
  reducers: {
    setAdultValue: (state, action) => {
      state.value = action.payload;
    },
    setChildValue:(state,action) => {
      state.value = action.payload;
    },
    setInfantValue :(state,action) => {
      state.value = action.payload;
    },
    increase: (state) => {
      state.value += 1;
    },
    decrease: (state) => {
      if (state.value > 0) {
        state.value -= 1;
      }
    },
  
  },
});

export const {
  setAdultValue,
  setChildValue,
  setInfantValue,
  increase,
  decrease,
} = tourSlice.actions;
export default tourSlice.reducer;
