import { createAsyncThunk, createSlice, isRejectedWithValue } from "@reduxjs/toolkit";

export const findTours = createAsyncThunk(
  "tour/tours",
  async (id, { isRejectedWithValue }) => {
    try {
      const response = await axios.get(`http://localhost:5000/tour/tours`);
      return response.data
    } catch (error) {
      return isRejectedWithValue(error)
    }
  }
)

const tourPanelSlice = createSlice({
  name: "panel",
  initialState: {
    id:null,
    privateTours: [],
    publicTours: [],
    title: null,
    img: null,
    numberOfPeople: null,
    transport: null,
    filteredTours:[],
    status: 'loading' || 'succeeded' || 'failed',
  },



  reducers: {
    setTitle: (state, action) => {
      state.title = action.payload;
    },
    setImg: (state, action) => {
      state.img = action.payload;
    },
    setNumberOfPeople: (state, action) => {
      state.numberOfPeople = action.payload;
    },
    setTransport: (state, action) => {
      state.transport = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(findTours.pending, (state) => {
        state.status = 'loading'
      })
      .addCase(findTours.fulfilled, (state, action) => {
        state.status = 'succeeded'
       state.publicTour.push({
        state
        // state.id = findTours.id;
        // state.title. findTours.title;
        // state.transport  = findTours.transport
       })

      })
      .addCase(findTours.rejected, (state, action) => {
        state.status = 'failed'
      })
  }

})

export const {setTitle,setImg,setNumberOfPeople,setTransport } = tourPanelSlice.actions;
export default tourPanelSlice.reducer;
