import {createSlice} from '@reduxjs/toolkit';
import Tuscany from '../DB/Tuscany.json'
const tourSlice = createSlice({
  name: "tour",
  initialState: {
    adultPrice: 0,
    childPrice: 0,
    infantPrice: 0,
    adultCost: 0,
    childCost: 0,
    infantCost: 0,
    totalCost: 0,
    value: 0,
    img:null,

  },
  reducers: {
    setValue: (state, action) => {
      state.value = action.payload;
    },
    increase: (state) => {
      state.value += 1;
    },
    decrease: (state) => {
      if (state.value > 0) {
         state.value -= 1;
      }
       
    },
    setAdultCost: (state, action) => {
      state.adultPrice = Tuscany.find((price)=> price.id === item.adultPrice
    );
      state.adultCost = action.payload.quantity * state.adultPrice;
    },
    setChildCost: (state, action) => {
      state.childPrice = action.payload.price;
      state.childCost = action.payload.quantity * state.childPrice;
    },
    setInfantCost: (state, action) => {
      state.infantPrice = action.payload.price;
      state.infantCost = action.payload.quantity * state.infantPrice;
    },
    setTotalCost: (state) => {
      state.totalCost = state.adultCost + state.childCost + state.infantCost;
    },
    setImgTour :(state)=> {
      const findImgById = Tuscany.map((img)=> img.id);
      state.img = state.findImgById
    },

  },
});

export const {
  setValue,
  increase,
  decrease,
  setAdultCost,
  setChildCost,
  setInfantCost,
  setTotalCost,

} = tourSlice.actions;
export default tourSlice.reducer;
