export default function SpecificTour({ id }) {
  const [selectedTour, setSelectedTour] = useState(null);

  useEffect(() => {
    const foundTour = Tuscany.find(tour => tour.id === Number(id));
    setSelectedTour(foundTour);
  }, [id, Tuscany]); // Ensure dependency array includes Tuscany

  return (
    <section>
      {selectedTour ? (
        <div>
          <img src={selectedTour.img} alt={selectedTour.title} />
          <h3>{selectedTour.title}</h3>
          <section className="price">
            <p>from <p>{selectedTour.price}</p></p>
          </section>
          <p>{selectedTour.short_description}</p>
          {/* ... other tour details ... */}
        </div>
      ) : (
        <p>Tour not found or data is loading...</p>
      )}
    </section>
  );
}
