import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { Icon } from "@iconify/react";
import { fetchTourById } from "../slices/tourById";

export default function TicketOverview({ nextLink, onNext, tourId, error, loading }) {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    if (tourId) {
      dispatch(fetchTourById(tourId));
    }
  }, [dispatch, tourId]);

  const { selectedDate, selectedTime } = useSelector((state) => state.datepicker);
  const {
    adultValue,
    childValue,
    infantValue,
    totalAdultPrice,
    totalChildPrice,
    totalInfantPrice,
    totalPrice,
  } = useSelector((state) => state.tour);


  const ticketData = useSelector(state => state.tour.ticketData);

  useEffect(() => {
    if (tourId) {
      dispatch(fetchTourById(id))
        .unwrap()
        .then((data) => console.log("✅ Tour fetched:", data))
        .catch((err) => console.error("❌ Fetch error:", err));
    }
  }, [dispatch, tourId]);

  if (!ticketData) return <p>Loading tour data...</p>;


  const handleClick = () => {
    if (onNext) onNext();
    navigate(nextLink);
  };

  if (loading) return <p>Loading...</p>;

  if (error)
    return (
      <p>
        Error:{" "}
        {typeof error === "string" ? error : error.message || JSON.stringify(error)}
      </p>
    );

  return (
    // <section >
    //   <h2>Your Tickets Overview</h2>
    //   {ticketData && (
    //   <section style={{ display: "flex", gap: "1.25em", alignItems: "center", flexWrap: "wrap" }}>
    //     <img
    //       src={ticketData.img}
    //       alt="Tour"
    //       style={{ width: "10em", height: "6.8em", borderRadius: "0.8em", objectFit: "cover" }}
    //     />
    //     <section style={{ display: "flex", flexDirection: "column", gap: "0.3em" }}>
    //       <h2>{ticketData.title}</h2>
    //       <section style={{ display: "flex", gap: "0.625em", alignItems: "center" }}>
    //         <Icon icon="system-uicons:calendar-month" color="#FA8B02" />
    //         <h3>{selectedDate}</h3>
    //       </section>
    //       <section style={{ display: "flex", gap: "0.625em", alignItems: "center" }}>
    //         <Icon icon="system-uicons:clock" color="#FA8B02" />
    //         <h3>{selectedTime}</h3>
    //       </section>
    //     </section>
    //   </section>

    //   <hr />

    //   <section>
    //     {/* Adult */}
    //     <section className="details">
    //       <section className="dest_price">
    //         <section className="pplAmount"><p>{adultValue}</p></section>
    //         <p style={{ color: "#333" }}>Adult (18+) (€{ticketData.adultPrice})</p>
    //       </section>
    //       <h3>€{totalAdultPrice}</h3>
    //     </section>

    //     {/* Child */}
    //     <section className="details">
    //       <section className="dest_price">
    //         <section className="pplAmount"><p>{childValue}</p></section>
    //         <p style={{ color: "#333" }}>Child (6-17) (€{ticketData.childPrice})</p>
    //       </section>
    //       <h3>€{totalChildPrice}</h3>
    //     </section>

    //     {/* Infant */}
    //     <section className="details">
    //       <section className="dest_price">
    //         <section className="pplAmount"><p>{infantValue}</p></section>
    //         <p style={{ color: "#333" }}>Infant (0-5) (€{ticketData?.infantPrice})</p>
    //       </section>
    //       <h3>€{totalInfantPrice}</h3>
    //     </section>
    //   </section>

    //   <hr />

    //   <section className="details">
    //     <h2 style={{ color: "#333" }}>Total Price</h2>
    //     <h2 style={{ color: "#FA8B02" }}>€{totalPrice}</h2>
    //   </section>

    //   <button onClick={handleClick} className="general_btn">
    //     <h3>Go to the Next Step</h3>
    //   </button>

    //   )}
    // </section>
    <section>
      <h2>Your Tickets Overview</h2>
      {ticketData && (
        <>
          <section style={{ display: "flex", gap: "1.25em", alignItems: "center", flexWrap: "wrap" }}>
            <img
              src={ticketData.img}
              alt="Tour"
              style={{ width: "10em", height: "6.8em", borderRadius: "0.8em", objectFit: "cover" }}
            />
            <section style={{ display: "flex", flexDirection: "column", gap: "0.3em" }}>
              <h2>{ticketData.title}</h2>
              <section style={{ display: "flex", gap: "0.625em", alignItems: "center" }}>
                <Icon icon="system-uicons:calendar-month" color="#FA8B02" />
                <h3>{selectedDate || "No date selected"}</h3>
              </section>
              <section style={{ display: "flex", gap: "0.625em", alignItems: "center" }}>
                <Icon icon="system-uicons:clock" color="#FA8B02" />
                <h3>{selectedTime || "No time selected"}</h3>
              </section>
            </section>
          </section>

          <hr />

          <section>
            {/* Adult */}
            <TicketField label="Adult (18+)" price={ticketData.adultPrice} value={adultValue} total={totalAdultPrice} />
            {/* Child */}
            <TicketField label="Child (6-17)" price={ticketData.childPrice} value={childValue} total={totalChildPrice} />
            {/* Infant */}
            <TicketField label="Infant (0-5)" price={ticketData.infantPrice} value={infantValue} total={totalInfantPrice} />
          </section>

          <hr />

          <section className="details">
            <h2 style={{ color: "#333" }}>Total Price</h2>
            <h2 style={{ color: "#FA8B02" }}>€{totalPrice}</h2>
          </section>
        </>
      )}
      <button onClick={handleClick} className="general_btn">
        <h3>Go to the Next Step</h3>
      </button>
    </section>

  );
}
const TicketField = ({ label, price, value, total }) => {
  if (!value || value <= 0) return null;
  return (
    <section className="details">
      <section className="dest_price">
        <section className="pplAmount"><p>{value}</p></section>
        <p style={{ color: "#333" }}>
          {label} (€{price})
        </p>
      </section>
      <h3>€{total}</h3>
    </section>
  );
};
