import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import tours from '../DB/Tuscany.json';

export default function SpecificTour() {
  const { id } = useParams(); // Access id from URL
  const [tour, setTours] = useState(null);

  useEffect(() => {
    const foundTour = tours.find(tour => tour.id === id); 
    setTour(foundTour);
  }, [id]);

  if (!tour) return <p>Loading...</p>;

  return (
    <section key={tourData.id}>
      <h2>{tourData.title}</h2>
      <p>{tourData.description}</p>
    </section>
  );
}
