import React, {  useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import ggl from "../../assets/home/icons/CheckIn/google.svg";
import { setFullName,setNewUser,setEmail,setPassword } from "../../slices/authSlice.js";
import { toast } from "react-toastify";
import axios from 'axios';
import close from '../../assets/home/icons/CheckIn/window-close.svg'
export default function CreateAccount() {

  const [isOpen,setIsOpen] = useState(false);
  const handleCloseModal = () => {
    setIsOpen(false);
    navigate('/')
  };
  useEffect(() => {
    setIsOpen(true);
  }, []);

  const dispatch = useDispatch();
  const navigate = useNavigate();
 
  const { currentUser, error } = useSelector((state) => state.auth);

  const fullName = useSelector((state) => state.auth.fullName);
  const email = useSelector((state)=> state.auth.email);
  const password = useSelector((state)=> state.auth.password);
   const createAccount = async(data) => {
    try{
      const response = await axios.post('http://localhost:3000/auth/createAccount',fullName, email, password);
      console.log(response.data);
      dispatch(set)

    }catch(error) {
      console.log(error);
    }
   }

   const handleSubmit = (e) => {
    e.preventDefault();
  
    if (!fullName || !email || !password) {
      toast.error("Please fill in all fields");
      return;
    }
  
    dispatch(setNewUser());
    createAccount();
   
  };
  
  
  useEffect(() => {
    if (currentUser) {
      navigate("/account/noTickets");
    }
  }, [currentUser, navigate]);
  

  return (
    

    <section className="modal">
 
   {isOpen && (
  
    <section
    
    className="auth">
      <section style={{display:'flex',gap:'6.25em'}}>
        <h1>Create Account</h1>
      <button 
      onClick={handleCloseModal}
      className="modal-close-btn ">
        <img src={close} alt="close the window"/>
      </button>
      </section>
      
    
      <form onSubmit={handleSubmit}>
        <label htmlFor="fullName">Name and Surname</label>
        <input
        value={fullName}
        onChange={(e) => dispatch(setFullName(e.target.value))}        
        name="fullName"
        placeholder="Enter your name and surname"
        />
        <label htmlFor="email">Email Address</label>
        <input
          type="email"
          name="email"
          value={email}
          onChange={(e)=> dispatch(setEmail(e.target.value))}
          placeholder="Enter your email address"
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          value={password}
          onChange={(e)=>dispatch(setPassword(e.target.value))}
          name="password"
          aria-describedby="password"
          aria-invalid="false"
          placeholder="Enter your password"
        />
        <section style={{display:'flex', gap:'0.8em',alignItems:'baseline'}}>
          <input 
          style={{height:'1em',width:'1em'}}
          required type="checkbox" />
          <p>
            I agree with <span style={{color:'#FA8B02'}}>Terms</span> and{" "}
            <span style={{color:'#FA8B02'}}>Privacy</span>
          </p>
        </section>
        <section style={{display:'flex',flexDirection:'column'}}>
          <button
            className="general_btn"
            style={{padding:'0.625em 1.5em 0.625em 1.5em'}}
            type="submit"
          >
            <span>Sign Up</span>
          </button>
          <p>or</p>
          <a href="http://localhost:3000/auth/google">
          
            <button 
            className="btn_ggl_submit"
             >
              <img src={ggl} alt="signIn with Google" />
              <span>Sign In with Google</span>
            </button>
            
          </a>
          
          <section className="additionals">
            <p>Already have an account?</p>
            <Link to="/auth/login"><span style={{color:"#FA8B02"}}>Log In</span></Link>
          </section>
        </section>
      </form>
    
      {error && <p style={{ color: "red" }}>{error}</p>}
    </section>
   )} 
   </section>
  
  );
}
