import React from 'react';
import { useParams,Link  } from 'react-router-dom';
import tourData from '../DB/Tuscany.json';
// import TuscanySlider from '../components/Carousel/TuscanyCarousel';
import BookingDetails from './Purchase/BookingDetails';

export default function SpecificTour() {
  const { id } = useParams(); 



  const tour = tourData.find((tour) => tour.id === parseInt(id));

  
  return (
    <section>
      <section className="tour" key={tour.id}>
        <h2>{tour.title}</h2> 
        <p> from </p><p><strong>Price:</strong> {tour.price} â‚¬</p>

        <img src={tour.img} alt={tourData.title} />
        <p>{tourData.short_description}</p>
        <section>
        {/* <Datepicker/>
        <TimePicker/> */}
        <Link to={<BookingDetails/>}>
        <button> Buy Now</button>
        </Link>
        

       <h3>Details</h3>
        <p>{tourData.details}</p>
        <img/> <h3> Number of group: {tourData.people}</h3>
        <img/> <h3><strong>Duration:</strong> {tourData.duration}</h3>   
        <img/> <h3> Departuring and arriving areas: {tourData.departure}</h3>
        <img/> <h3> Guide service:{tourData.guide}</h3>
        <img/> <h3>Language:{tourData.language}</h3>
        <img/> <h3>Entry Fees:{tourData.fees} </h3>
        <img/>  <h3> EntryTransportation:{tourData.transport}</h3>
             </section>

      </section>

    {/* <TuscanySlider/> */}
    </section>
  );
}

