import React, { useState , useContext} from 'react';
import { useParams, Link} from 'react-router-dom';
import toursData from '../DB/Tuscany.json';
// import BookingDetails from './Purchase/BookingDetails';

import group from '../assets/home/icons/Tours/group_col.svg';
import duration from '../assets/home/icons/Tours/duration.svg';
import location from '../assets/home/icons/Footer/location.svg';
import guide from '../assets/home/icons/guide.svg';
import lang from '../assets/home/icons/Tours/language.svg';
import fees from '../assets/home/icons/Tours/ticket.svg';
import transport from '../assets/home/icons/Tours/transport.svg';
import { MainGallery } from '../components/MainGallery';
import { GalleryPlaces } from '../components/GalleryPlaces';
import Testimotionals from '../components/Carousel/TestimonialsCarousel';
import back from '../assets/home/icons/CheckIn/grey_arrow.svg';
import TimePicker from '../components/Picker/TimePicker';
import DatePicker from '../components/Picker/DatePicker';
import { DateContext } from "../../context/DateContext";


import Tours from '../pages/Tours'
export default function SpecificTour() {
    const { id } = useParams();
    const tours = toursData.find((tours) => tours.id === parseInt(id));

    // Check if tours is undefined
    if (!tours) {
        return <p>Tour not found</p>;
    }
    console.log(tours)
    const { selectedDate, setSelectedDate, selectedTime, setSelectedTime } = useContext(DateContext);

    const handleDateChange = (newDate) => setSelectedDate(newDate);
  
    const handleTimeChange = (newTime) => setSelectedTime(newTime);
    // const [hour,setHour] = useState(null);
    // const [minute,setMinute] = useState(null);
    

    // const Time = [
    //     {id:0,hour:"1:00 a.m",value:{hour:13,minute:0}},
    //     {id:1,hour:"2:00 a.m",value:{hour:14,minute:0}},
    //     {id:2,hour:"3:00 a.m",value:{hour:15,minute:0}},
    //     {id:3,hour:"4:00 a.m",value:{hour:16,minute:0}},
    //     {id:4,hour:"5:00 a.m",value:{hour:17,minute:0}}
    // ];
    // const changeTime = (time) => {
    //     setHour(time.hour);
    //     setMinute(time.minute);
    // }
    // const formatHour = (hour,minute) => {
    //     setHour(hour.toDateString)
    //     setMinute(minute.toDateString);
    // }
    return (
        <section className='specifictour'>
        <section >
            <Link to="/tours" element={<Tours/>}>
                <span className='btn_back'>
                    <img src={back}/><p>Back</p>
                </span>
            </Link>
        </section>

        <section className='main_specT'>
            <MainGallery/>
            <section className="tour" key={tours.id}>
                    <section className='spectTour_intro'>
                            <h2>{tours.title}</h2> 
                        <section className='specTour_price'>
                            <h2>from</h2> <p><strong>{tours.price} â‚¬</strong> </p>
                        </section>
                            <h3>{tours.short_description}</h3>

                    </section>

                        <section className='spectTour_intro'>
                            <h3>Select a date</h3>
                            <DatePicker
                            value={selectedDate}
                            onChange={handleDateChange}
                            />
                        </section>

                        <section className='spectTour_intro'>
                            <h3>Time</h3>
                            <section className="book_set">
                           <TimePicker
                           value={selectedTime}
                           onChange={handleTimeChange}
                           />
                            </section>
                        </section>
                            <Link to={'/BookingDetails/${id}'}>
                                <button className='btn_submit'>Buy Now</button>
                            </Link>
                   
    </section>
        </section>
                <section className='detail_container'>
                            <h3>Details</h3>
                                    <p>{tours.details}</p>
                                    <section className='detail'>
                                        <img src={group} alt='number of people in the group'/> 
                                        <h3>Number of group: {tours.people}</h3>
                                    </section>
                                    <section className='detail'>
                                        <img src={duration} alt='trip duration'/> 
                                        <h3><strong>Duration:</strong> {tours.duration}</h3>   
                                    </section>

                                    <section className='detail'>
                                        <img src={location} alt='departure area'/> 
                                        <h3>Departuring and arriving areas: {tours.departure}</h3>
                                    </section>

                                    <section className='detail'>
                                        <img src={guide} alt='guide'/> 
                                        <h3>Guide service: {tours.guide}</h3>
                                    </section>

                                    <section className='detail'>
                                        <img src={lang} alt='language tour'/> 
                                        <h3>Language: {tours.language}</h3>
                                    </section>

                                    <section className='detail'>
                                        <img src={fees} alt='entry fee'/> 
                                        <h3>Entry Fees: {tours.fees}</h3>
                                    </section>

                                    <section className='detail'> 
                                        <img src={transport} alt='available transport'/>  
                                        <h3>Transportation: {tours.transport}</h3>
                                    </section>
                </section>
            <GalleryPlaces/>   
            
            <Testimotionals id={tours.id}  />
   
    </section>
    );
}
