import React, { useState, useEffect, useParams } from 'react';
import tours from'../DB/Tuscany.json'

export default function SpecificTour() {
  const { id } = useParams(); // Access id from URL
  const [tourData, setTourData] = useState(null);

  const tours = tours.find((tour)=> SpecificTour);

  useEffect(() => {
    const foundTour = tours.find(tour => tour.id === id); // Find tour based on id
    setTourData(foundTour);
  }, [id, tours]);

  if (!tourData) return <p>Loading...</p>;

  return (
    <section key={tourData.id}>
      <h2>{tourData.title}</h2>
      <p>{tourData.description}</p>
    </section>
  );
}
