import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchTourById } from "../../slices/tourSlice";

import ProgressBar from "../../components/ProgressBar";
import DatePicker from "../../Picker/DatePicker";
import TicketOverview from "../../components/TicketOverview";

import dateIcon from "../../assets/home/icons/Tours/date.svg";
import timeIcon from "../../assets/home/icons/Tours/time.svg";



export default function BookingDetails() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { ticketData, loading, error } = useSelector((state) => state.tour);

  useEffect(() => {
    dispatch(fetchTourById(id));
  }, [dispatch, id]);

  

  const selectedDate = useSelector((state) => state.datepicker.selectedDate);
  const selectedTime = useSelector((state) => state.datepicker.selectedTime);

  // // ticket data
  // const adultPrice = useSelector((state) => state.tour.ticketData.adultPrice);
  // const adultValue = useSelector((state) => state.tour.ticketData.adultValue);
  // const childPrice = useSelector((state) => state.tour.ticketData.childPrice);
  const childValue = useSelector((state) => state.tour.ticketData.childValue);
  const infantPrice = useSelector((state) => state.tour.ticketData.infantPrice);
  const infantValue = useSelector((state) => state.tour.ticketData.infantValue);


if (loading) return <p>Loading...</p>;
  
  if (error)
    return (
      <p>
        Error:{" "}
        {typeof error === "string"
          ? error
          : error.message || JSON.stringify(error)}
      </p>
    );

  if (!ticketData) return <p>No data</p>;
  return (
    <>
      <ProgressBar />
      <section className="booking_container">
        <section>
          <section className="booking_data">
            <h2>When you will visit?</h2>
            <section style={{ display: "flex", alignItems: "center" }}>
              <img src={dateIcon} alt="choose a date" />
              <select>
                <option>{selectedDate || "Choose a date"}</option>
              </select>
              {!selectedDate && <DatePicker />}
            </section>
          </section>
          <section className="booking_data">
            <h2>Which time?</h2>
            <section style={{ display: "flex", alignItems: "center" }}>
              <img src={timeIcon} alt="choose a time" />
              <select>
                <option>{selectedTime || "Choose a time"}</option>
              </select>
            </section>
          </section>

          <ul>
            <h2>Select Your Tickets</h2>
            <li>Free for kids under 6 and disabled visitors (74%+)</li>
            <li>
              Pregnant women, families with strollers, and visitors on crutches
              can buy priority tickets at the venue
            </li>
          </ul>

          <section 
          key={ticketData.id}
          className="tickets_body">
            {/* Adult */}
            <TicketCounter
              label="Adult (18+)"
              price={ticketData.adultPrice}
              value={ticketData.adultValue}
              increase={() => dispatch(increaseAdultValue())}
              decrease={() => dispatch(decreaseAdultValue())}
              setValue={(val) => dispatch(setAdultValue(val))}
            />
            {/* Child */}
            <TicketCounter
              label="Child (6-17)"
              price={ticketData.childPrice}
              value={ticketData.childValue}
              increase={() => dispatch(increaseChildValue())}
              decrease={() => dispatch(decreaseChildValue())}
              setValue={(val) => dispatch(setChildValue(val))}
              info="With valid ID. Only in combination with: Adult (18+)"
            />
            {/* Infant */}
            <TicketCounter
              label="Infant (0-5)"
              price={ticketData.infantPrice}
              value={infantValue}
              increase={() => dispatch(increaseInfantValue())}
              decrease={() => dispatch(decreaseInfantValue())}
              setValue={(val) => dispatch(setInfantValue(val))}
              info="Only in combination with: Adult (18+)"
              freeLabel="FREE"
            />
          </section>
        </section>
        <TicketOverview nextLink={"/User"} />
      </section>
    </>
  );
}

const TicketCounter = ({ label, price, value, increase, decrease, setValue, info, freeLabel }) => (
  <section className="tickets_order">
    <ul>
      <h2>{label}</h2>
      {info && <li>{info}</li>}
      <h3 style={{ color: "#FA8B02" }}>
        â‚¬{price !== null && price !== undefined ? price : freeLabel || "Not available"}
      </h3>
    </ul>
    <section className="counter">
      <button onClick={decrease} disabled={value <= 0}>
        -
      </button>
      <input
        type="number"
        min={0}
        value={value}
        onChange={(e) => {
          const val = Math.max(0, Number(e.target.value));
          setValue(val);
        }}
      />
      <button onClick={increase}>+</button>
    </section>
  </section>
);
