import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getTourById, setTicketInfo } from '../';

const BookingDetails = () => {
  const dispatch = useDispatch();
  const { tourId } = useParams();
  const currentTour = useSelector((state) => state.tour.currentTour);

  useEffect(() => {
    if (tourId) {
      dispatch(getTourById(tourId));
    }
  }, [tourId, dispatch]);

  useEffect(() => {
    if (currentTour) {
      dispatch(setTicketInfo({
        price: currentTour.price,
        title: currentTour.title,
        image: currentTour.image,
      }));
    }
  }, [currentTour, dispatch]);

  // Handle loading or not found
  if (!currentTour) {
    return <div>Loading tour details...</div>; // Or a spinner
  }

  return (
    <section className="bg-[#f2f2f2] px-8 sm:px-24 py-12">
      <h2 className="text-3xl font-bold mb-4">Booking details</h2>
      <div className="bg-white p-4 rounded-xl">
        <div className="grid grid-cols-2 gap-10">
          <div>
            <img
              src={currentTour.image}
              alt={currentTour.title}
              className="rounded-xl max-h-[400px] w-full object-cover"
            />
          </div>
          <div className="flex flex-col gap-5 justify-between py-3">
            <div>
              <h2 className="text-3xl font-semibold mb-3">{currentTour.title}</h2>
              <p className="text-gray-500">{currentTour.description}</p>
            </div>
            <div className="text-end font-bold text-2xl">
              ${currentTour.price}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BookingDetails;
