import React from 'react';
import { useParams,Link  } from 'react-router-dom';
import tour from '../DB/Tuscany.json';
import BookingDetails from './Purchase/BookingDetails';
import Arrows from '../components/Carousel/Arrow';
import { ToursContext } from '../context/ToursContext';

import group from '../assets/home/icons/Tours/group_col.svg';
import duration from '../assets/home/icons/Tours/duration.svg';
import location from '../assets/home/icons/Footer/location.svg';
import guide from '../assets/home/icons/guide.svg';
import lang from '../assets/home/icons/Tours/language.svg';
import fees from '../assets/home/icons/Tours/ticket.svg';
import transport from '../assets/home/icons/Tours/transport.svg';
import { useContext } from 'react';


export default function SpecificTour() {
  const { id } = useParams();
  const tours = useContext(ToursContext);
  const tour = tours.find((t) => t.id === parseInt(id, 10)) ;

  if (!tour) {
    return <p>Tour not found</p>;
  }

  
  return (
    <section className='specificTour_main_container'>
      <section className="tour" key={tour.id}>
        <h2>{tour.title}</h2> 
        <p> from </p> <p><strong>Price:</strong> {tour.price} â‚¬</p>
      <section>

      </section>
<MainGallery/>
        <p>{tour.short_description}</p>
        <section>
      
         <Link to={<BookingDetails/>}>
        <button> Buy Now</button>
        </Link>
         
       <h3>Details</h3>
        <p>{tour.details}</p>
        <img src={group} /> <h3> Number of group: {tour.people}</h3>
        <img src={duration}/> <h3><strong>Duration:</strong> {tour.duration}</h3>   
        <img src={location}/> <h3> Departuring and arriving areas: {tour.departure}</h3>
        <img src={guide}/> <h3> Guide service:{tour.guide}</h3>
        <img src={lang}/> <h3>Language:{tour.language}</h3>
        <img src={fees}/> <h3>Entry Fees:{tour.fees} </h3>
        <img src={transport}/>  <h3> EntryTransportation:{tour.transport}</h3>
      </section>

      </section>
<GalleryPlaces/>
   
    </section>
  );
}

export const GalleryPlaces = () => {
  const { id } = useParams(); 

    const gallery = tour.find((gallery) => gallery.id === parseInt(id));
const { img1, img2, img3, img4 } = gallery;


return(
 
  <section  >
    <section className='carousel_header'>
        <h3>Gallery</h3>
<Arrows/>
    </section>
  
     <section className='carousel_container' key={gallery.id}> 
      <img className='carousel_item large' src={gallery.img4} alt="place's image 1" />
      <img  className='carousel_item top-small'src={gallery.img5} alt="place's image 2" />
      <img className='carousel_item  bottom-small' src={gallery.img6} alt="place's image 3" />
      <img className='carousel_item full' src={gallery.img7} alt="place's image 4" />
    </section>
  </section>
   
  
)
}

export const MainGallery = () => {
  const { id } = useParams(); 

  const gallery = tour.find((gallery) => gallery.id === parseInt(id));
  return(
    
    <section className='main_gallery' key={gallery.id}>
      <img  className='main_gallery-first' src={gallery.img}/>
      <img className='main_gallery-item' src={gallery.img1} />
      <img className='main_gallery-item' src={gallery.img2} />
      <img className='main_gallery-item' src={gallery.img3} />
    </section>

  )
}
