import { apiSlice } from "./apiSlices";

const USERS_URL = '/users';

export const usersApiSlice = apiSlice.injectEndpoints({
    endpoints:(builder) => ({
        login:builder.mutation({
            query:(data) =>({
                url:`${USERS_URL}/auth`,
                method:'POST',
                body:data
            }) 
        }),
        logout
    }),
})

export const  {useLoginMutation} = usersApiSlice;
